rules.errorMessageExpression:
(() => {
    let taskReportInfo = data.project_info[0].task_report_info;
    let sumWorkHours = 0;
    let retStr = '';
    for (let i = 0; i < taskReportInfo.length; i++) {
        sumWorkHours = utils.add(taskReportInfo[i].work_hours, sumWorkHours);

    }

    if (executeContext.locale === 'zh_TW') {
        retStr =  '工時與執行人清單累計工時('+sumWorkHours+')未保持一致';
    } else {
        retStr =  '工时与执行人清单累计工时('+sumWorkHours+')未保持一致';
    }
    return retStr;
})()
